$(function() {
    $(".ind_MV_slide").slick({
        arrows: !1,
        initialSlide: 3,
        autoplay: !0,
        adaptiveHeight: !0,
        centerMode: !0,
        centerPadding: "18%",
        dots: !1,
        speed: 1200,
        autoplaySpeed: 3000,
        pauseOnFocus: !1,
        pauseOnHover: !1,
        asNavFor: ".ind_MV_thumbnail",
        responsive: [{
            breakpoint: 1250,
            settings: {
                centerPadding: "10%",
            },
        }, {
            breakpoint: 750,
            settings: {
                centerPadding: "0%",
            },
        }, ],
    });
    $(".ind_MV_thumbnail").slick({
        fade: !0,
        asNavFor: ".ind_MV_slide",
        arrows: !1,
    })
});
const jsLoad = '#Loading';
const jsLoadIllust = '.Loading_illust';
const jsLoadIllustli = '.Loading_illust li';
const jsLoadBg = '.Loading_bg';
gsap.set(jsLoadIllust, {
    opacity: 0,
});
gsap.set(jsLoadIllustli, {
    opacity: 0,
});
gsap.set(jsLoadBg, {
    opacity: 0,
    x: '-100%',
});
const tl = gsap.timeline();
tl.to(jsLoadIllust, {
    opacity: 1,
    duration: 0.1,
}, ).to(jsLoadIllustli, {
    opacity: 1,
    duration: 0.3,
    delay: 1,
    stagger: {
        amount: 2,
        from: "start",
    }
}, '-=0.5').to(jsLoadIllustli, {
    opacity: 0,
    duration: 0.2,
}, '+=0.5').to(jsLoadBg, {
    opacity: 1,
    duration: 0.2,
}, ).to(jsLoadBg, {
    x: '0',
    duration: 0.5,
    ease: "power1.out",
}, ).to(jsLoad, {
    x: '100%',
    duration: 0.5,
    ease: "power1.out",
}, '+=0.3')
